  void _updateWheatMarkers(Set<Marker> markers) {
    setState(() {
      wheatMarker = markers;
    });
  }

  void _updateRescueMarkers(Set<Marker> markers) {
    setState(() {
      rescueMarker = markers;
    });
  }

  void _updateHarvesterMarkers(Set<Marker> markers) {
    setState(() {
      harvesterMarker = markers;
    });
  }

  Future<Marker> Function(Cluster<DisplayPoint>) _markerBuilder(
          {required String svgPath}) =>
      (cluster) async {
        return Marker(
            markerId: MarkerId(cluster.getId()),
            position: cluster.location,
            onTap: () async {
              if (cluster.isMultiple) {
                final zoomLevel = await _controller.future
                    .then((value) => value.getZoomLevel());
                _controller.future.then((value) => value.moveCamera(
                    CameraUpdate.newCameraPosition(CameraPosition(
                        target: cluster.location,
                        zoom: _getNextLevel(zoomLevel, _levels)))));
              } else {
                MapsSheet.show(
                  context: context,
                  destination: LatLng(
                    cluster.items.first.latitude,
                    cluster.items.first.longitude,
                  ),
                  type: cluster.items.first.type,
                );
              }
            },
            icon: await GoogleMapIcon(
                    imagePath: svgPath, isMultip: cluster.isMultiple)
                .toBitmapDescriptor());
      };

  double _getNextLevel(double value, List<double> levels) {
    double result = 0.0;
    final index = levels.indexOf(
        levels.firstWhere((val) => val > value, orElse: () => levels.last));
    if (index == levels.length - 1) {
      result = levels[index];
    } else {
      result = levels[index];
    }
    return result;
  }

